import Router from '../../router';
import Usuario from './usuario.service';
import {mdiAccount, mdiDelete, mdiMagnify, mdiPencil} from '@mdi/js'


export default {
    Router,
    data: () => ({
        icons: {
            mdiAccount,
            mdiMagnify,
            mdiPencil,
            mdiDelete,
        },
        msgDeAlertas: {
            success: '',
            error: '',
            info: '',
            warning: '',
        },
        usuarios: [],
        loading: false,
        pesquisaRapida: '',
        headers: [
            {
                text: 'Nome',
                align: 'left',
                value: 'nome',
            },
            {
                text: 'E-mail',
                align: 'left',
                value: 'email'
            },
            {
                text: 'Data Criação',
                align: 'center',
                value: 'dataCriacao'
            },
            {
                text: 'Tipo de Usuário',
                align: 'center',
                value: 'tipoUsuario'
            },
            {
                text: 'Editar',
                value: 'editar',
                align: 'center',
                sortable: false
            },
            {
                text: 'Excluir',
                value: 'excluir',
                align: 'center',
                sortable: false,
                showgroupby: false,
            },
        ],
    }),

    created() {
        this.initialize()
    },

    methods: {
        initialize() {
            this.loading = true;
            Usuario.listar().then(resposta => {
                this.usuarios = resposta.data;
                this.loading = false;
            }).catch(onError => {
                if (onError.response && onError.response.data) {
                    this.onError(onError.response.data.message);
                } else {
                    this.onError(onError);
                }
                this.loading = false;
            });
        },

        editItem(item) {
            if (item && item.oid) {
                this.$router.push({path: `/usuario/${item.oid}`});
            }
        },
        excluir(item) {
            this.$swal.mixin({
                customClass: {
                    confirmButton: 'btn btn-success',
                    cancelButton: 'btn btn-danger'
                },
                buttonsStyling: false
            });
            this.$swal.fire({
                position: 'top',
                title: 'Deseja excluir esse usuário?',
                type: 'question',
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'Sim, excluir!',
                cancelButtonText: 'Não, cancelar!',
                confirmButtonColor: 'hsl(119, 37%, 50%)',
                cancelButtonColor: '#d33',
                reverseButtons: true
            }).then(resposta => {
                if (resposta.value) {
                    Usuario.bloquear(item).then(resposta => {
                        this.onSuccess(resposta.data);
                        this.close();
                    }).catch(onError => {
                        if (onError.response && onError.response.data) {
                            this.onError(onError.response.data.message);
                        } else {
                            this.onError(onError);
                        }
                        this.close();
                    });
                } else {
                    this.onCancelado();
                    this.close();
                }
            });
        },
        close() {
            setTimeout(() => {
                this.initialize();
            }, 300);
        },
        onSuccess(msg) {
            this.$swal.fire({
                position: 'top',
                type: 'success',
                title: 'Sucesso',
                text: msg,
                showConfirmButton: false,
                timer: 2000
            });
        },
        onError(msg) {
            this.$swal.fire({
                position: 'top',
                type: 'error',
                title: 'Erro',
                text: msg,
                timer: 2000,
            })
        },
        onCancelado(msg) {
            this.$swal.fire({
                position: 'top',
                type: 'error',
                title: 'Cancelado',
                text: msg,
                timer: 2000,
            })
        },
    },
    components: {},
};
