import {mdiMagnify} from '@mdi/js';
import ZoomEmpresas from './zoom-empresas.service';

export default {
    mounted() {
        this.innerMethod();
    },
    name: "ZoomEmpresas",
    props: ['oidEmpresa'],
    data: () => ({
        icons: {
            mdiMagnify,
        },
        dialog: false,
        formTitle: 'Zoom De Empresas',
        headers: [
            {
                text: 'Nome',
                align: 'center',
                value: 'nome'
            },
            {
                text: 'CNPJ',
                align: 'center',
                value: 'cnpj'
            },
            {
                text: 'Turno',
                align: 'center',
                value: 'turno'
            }
        ],
        loading: false,
        pesquisaRapida: '',
        singleSelect: true,
        empresaSelecionada: [],
        empresas: [],
        empresa: {},
    }),
    methods: {
        initialize() {
            this.loading = true;
            ZoomEmpresas.listar().then(resposta => {
                this.dialog = true;
                this.empresas = resposta.data;
                this.loading = false;
            }).catch(onError => {
                this.msgDeAlertas.error = onError;
            });
        },
        consultaPeloOid() {
            if (this.oidEmpresa) {
                ZoomEmpresas.buscaEmpresaOid(this.oidEmpresa).then(resposta => {
                    this.empresa = resposta.data;
                }).catch(onError => {
                    if (onError.response && onError.response.data) {
                        this.onError(onError.response.data.message);
                    } else {
                        this.onError(onError);
                    }
                })
            }
        },
        close() {
            this.dialog = false;
            this.empresaSelecionada = [];
            this.empresas = [];
        },
        selecionar() {
            if (this.empresaSelecionada && this.empresaSelecionada.length > 0) {
                if (this.empresaSelecionada) {
                    this.empresa = this.empresaSelecionada[0];
                    this.$emit('empresaSelecionada', this.empresa);
                }
                this.close();
            } else {
                this.onError('Falha ao selecionar Empresa');
                this.close();
            }
        },
        onSuccess(msg) {
            this.$swal.fire({
                position: 'top',
                type: 'success',
                title: 'Sucesso',
                text: msg,
                showConfirmButton: false,
                timer: 2000
            });
        },
        onError(msg) {
            this.$swal.fire({
                position: 'top',
                type: 'error',
                title: 'Erro',
                text: msg,
                timer: 2000,
            })
        },
        innerMethod() {
            this.consultaPeloOid();
        },
    },
}
